import "./App.css";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Header from "./components/header";
import RegistrationForm from "./components/registration";
import Login from "./components/login";
import CourseForm from "./components/courseRegistration";
import registerService from "./services/register";
import Exit from "./components/exit";
import { confirmAlert } from "react-confirm-alert";

function App() {
  const [userData, setUserData] = useState(null);

  const alertBox = (message) => {
    confirmAlert({
      title: "Notification",
      message: message,
      buttons: [
        {
          label: "Ok",
          onClick: () => {
            const currList = window.localStorage.getItem("currentList");
            const courseList = window.localStorage.getItem("courseList");
            if (currList || courseList?.length === 2) {
              window.localStorage.removeItem("loggedInUser");
              window.localStorage.removeItem("courseList");
            }
          },
        },
      ],
    });
  };

  useEffect(() => {
    const loggedInUser = window.localStorage.getItem("loggedInUser");
    if (loggedInUser) setUserData(JSON.parse(loggedInUser));
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Login Page */}
          <Route
            path="/login"
            element={<Login onLogin={(user) => setUserData(user)} />}
          />

          {/* Registration Page */}
          <Route
            path="/register"
            element={
              userData ? (
                <RegistrationForm/>
              ) : (
                <Navigate to="/login" />
              )
            }
          />

          {/* Course Registration */}
          <Route
            path="/courses"
            element={
              userData ? (
                <CourseForm userData={userData} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />

          {/* Default Route */}
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
